const Header = ({ name }) => <h2>{name}</h2>
//const Total = ({ sum }) => <p>Number of exercises {sum}</p>
const Part = ({ part }) =>
  <p> {part.name} {part.exercises} </p>

const Content = ({ parts }) => {
  // <>
  //   <Part part={parts[0]} />
  //   <Part part={parts[1]} />
  //   <Part part={parts[2]} /> 
  //   <Part part={parts[3]} />      
  // </>
  return (
    <> {parts.map((part) => <Part key={part.id} part={part} />)} </>
  )
}



const Total = ({ course }) => {
  const total = course.parts.reduce(
    (total, part) => { return total + part.exercises }, 0);
  return (
    <p><b>Total of {total} exercises</b></p>
  )
}


const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    },
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]

  return (courses.map(
    (course) => <Course key={course.id} course={course} />
  ));
}

export default App
